import { createSlice } from '@reduxjs/toolkit';

const initialState = { cartItems: [], totalQuantity: 0, showCart: false };

const cartSlice = createSlice({
    name: 'cart',
    initialState,
    reducers: {
        addItemToCart(state, action) {
            const newItem = action.payload;
            const existingItem = state.cartItems.find(
                (item) => item.id === newItem.id
            );
            if (!existingItem) {
                state.cartItems.push({
                    title: newItem.title,
                    price: newItem.price,
                    quantity: 1,
                    id: newItem.id,
                });
            } else {
                existingItem.quantity++;
            }
            state.totalQuantity++;
        },
        removeItemFromCart(state, action) {
            const itemRemoved = action.payload;
            const existingItem = state.cartItems.find(
                (item) => item.id === itemRemoved.id
            );
            if (existingItem.quantity === 1) {
                state.cartItems = state.cartItems.filter(
                    (item) => item.id !== itemRemoved.id
                );
            } else {
                existingItem.quantity--;
            }
            state.totalQuantity++;
        },
        toggleCart(state) {
            state.showCart = !state.showCart;
        },
    },
});

export const sendCartData = async (cartData) => {
    const sendData = (cart) => {
        dispatch(
            uiActions.showNotification({
                status: 'pending',
                title: 'Sending...',
                message: 'Sending cart data!',
            })
        );
        axios.put(
            'https://redux-max-project-default-rtdb.firebaseio.com/cart.json',
            {
                body: JSON.stringify(cart),
            }
        );
    };

    try {
        await sendData(cartData);

        dispatch(
            uiActions.showNotification({
                status: 'success',
                title: 'Success!',
                message: 'Sent cart data successfully!',
            })
        );
    } catch (error) {
        dispatch(
            uiActions.showNotification({
                status: 'error',
                title: 'Error!',
                message: 'Sending cart data failed!',
            })
        );
    }
};

export const cartActions = cartSlice.actions;
export const cartReducer = cartSlice.reducer;
