import Card from '../UI/Card';
import classes from './Cart.module.css';
import CartItem from './CartItem';

const Cart = () => {
    return (
        <Card className={classes.cart}>
            <h2>Your Shopping Cart</h2>
            <ul>
                {cartItems.map((item) => (
                    <CartItem
                        item={{
                            title: item.title,
                            quantity: item.quantity,
                            total: item.quantity * item.price,
                            price: item.price,
                            id: item.id,
                        }}
                    />
                ))}
            </ul>
        </Card>
    );
};

export default Cart;
